(3402, 65, 'section_builder_0_layout_section_left', 'Content Field (WYSIWYG)'),
(3403, 65, '_section_builder_0_layout_section_left', 'field_54c7f624fb8dd'),
(3404, 65, 'section_builder_0_layout_section_left_content', 'We envision <span class="blue-background">multifaith</span> <span class="blue-background">movements</span> for <span class="blue-background">justice</span> that are so irresistably inspiring, powerfully organized, sustained, and deeply rooted in our faiths that we spark wide-reaching change. We see our unique call to echo and amplify a call for justice from faith-rooted movement leaders with strategic advice, digital tools, and training.'),
(3405, 65, '_section_builder_0_layout_section_left_content', 'field_54c7f9a30549b'),
